import React from "react";
import styled from "styled-components/macro";
import { Section } from "../components";

/* --- Full-bleed container (no side white space) --- */
const FullBleed = styled(Section.InnerContainer)`
  width: 100%;
  max-width: none;
  padding: clamp(48px, 8vw, 96px) clamp(16px, 3vw, 32px);
`;

/* --- Hero row --- */
const Hero = styled.div`
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  gap: clamp(24px, 4vw, 48px);
  align-items: center;

  @media (max-width: 980px) {
    grid-template-columns: 1fr;
  }
`;

const Copy = styled.div``;

const Eyebrow = styled.p`
  font-weight: 700;
  letter-spacing: .06em;
  text-transform: uppercase;
  color: var(--bs-primary, #2563eb);
  margin: 0 0 6px;
`;

const Title = styled.h2`
  margin: 0 0 10px;
  font-size: clamp(28px, 4vw, 42px);
  line-height: 1.15;
  color: var(--bs-gray-900, #111);
`;

const Lead = styled.p`
  margin: 10px 0 20px;
  max-width: 60ch;
  font-size: 1.1rem;
  line-height: 1.7;
  color: var(--bs-gray-700, #4b5563);
`;

/* --- CTA buttons --- */
const Buttons = styled.div`
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
  margin: 12px 0 10px;
`;

const GhostBtn = styled.a`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  border-radius: 12px;
  border: 1px solid rgba(0,0,0,.08);
  background: #fff;
  color: var(--bs-gray-900, #111);
  text-decoration: none;
  font-weight: 600;
  transition: transform .15s ease, box-shadow .15s ease;

  &:hover { transform: translateY(-1px); box-shadow: 0 10px 20px rgba(0,0,0,.06); }
`;

/* --- Quick actions (phone / email / visit) --- */
const QuickActions = styled.div`
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 10px;
  margin-top: 12px;

  @media (max-width: 520px) {
    grid-template-columns: 1fr 1fr;
  }
`;

const Action = styled.a`
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 14px;
  border-radius: 12px;
  background: rgba(255,255,255,.7);
  backdrop-filter: blur(4px);
  text-decoration: none;
  color: var(--bs-gray-900, #111);
  border: 1px solid rgba(0,0,0,.06);
  transition: transform .15s ease, box-shadow .15s ease;

  &:hover { transform: translateY(-1px); box-shadow: 0 10px 16px rgba(0,0,0,.06); }
`;

const Icon = styled.span`
  width: 36px; height: 36px; min-width: 36px;
  display: inline-grid; place-items: center;
  border-radius: 50%;
  background: var(--bs-primary-100, #e0e7ff);
  font-size: 18px;
`;

/* --- Decorative panel on the right side --- */
const Panel = styled.div`
  height: clamp(260px, 34vw, 420px);
  border-radius: 20px;
  background:
    radial-gradient(120% 120% at 10% 0%, rgba(37,99,235,.15), transparent 50%),
    radial-gradient(120% 120% at 90% 100%, rgba(16,185,129,.15), transparent 50%),
    linear-gradient(180deg, #ffffff, #f8fafc);
  box-shadow: 0 20px 40px rgba(0,0,0,.08);
  border: 1px solid rgba(0,0,0,.06);
`;

/* --- Feature cards grid --- */
const Grid = styled.div`
  margin-top: clamp(28px, 4vw, 44px);
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 18px;
`;

const Card = styled.div`
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 10px 24px rgba(0,0,0,.06);
  border: 1px solid rgba(0,0,0,.05);
  padding: 22px 22px 20px;
  transition: transform .15s ease, box-shadow .15s ease;
  &:hover { transform: translateY(-2px); box-shadow: 0 18px 36px rgba(0,0,0,.08); }

  h3 {
    margin: 0 0 8px;
    font-size: 1.15rem;
    font-weight: 700;
    color: var(--bs-gray-900, #111);
  }
  p {
    margin: 0;
    color: var(--bs-gray-700, #4b5563);
    line-height: 1.65;
  }
`;

/* --- Small stats row --- */
const Stats = styled.div`
  margin-top: 22px;
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 12px;

  @media (max-width: 640px) { grid-template-columns: 1fr 1fr; }
`;

const Stat = styled.div`
  background: #fff;
  border: 1px solid rgba(0,0,0,.06);
  border-radius: 14px;
  padding: 16px;
  text-align: center;

  strong { display:block; font-size: 1.4rem; }
  span { color: var(--bs-gray-600, #6b7280); }
`;

const HomeContactContainer = () => {
  return (
    <Section bgColor="--bs-fade-blue">
      <FullBleed>
        <Hero>
          <Copy>
            <Eyebrow>Need help fast?</Eyebrow>
            <Title>Talk to a real person—anytime</Title>
            <Lead>
              Questions about a listing, pricing, or bookings? Our support team
              will get you the answer (usually within minutes).
            </Lead>

            <Buttons>
              {/* If you have Section.Button, it will render nicely; otherwise swap for a link */}
              <Section.Button as="a" href="/contact">Talk to an Agent</Section.Button>
              <GhostBtn href="/faq">Browse FAQs</GhostBtn>
            </Buttons>

            <QuickActions>
              <Action href="tel:+962000000000">
                <Icon>📞</Icon><span>Call us</span>
              </Action>
              <Action href="mailto:hello@example.com">
                <Icon>✉️</Icon><span>Email</span>
              </Action>
              <Action href="/visit">
                <Icon>📍</Icon><span>Visit office</span>
              </Action>
            </QuickActions>
          </Copy>

          {/* Decorative / illustration panel */}
          <Panel aria-hidden="true" />
        </Hero>

        {/* Why contact us */}
        <Grid>
          <Card>
            <h3>Local Experts</h3>
            <p>Get advice from agents who know the neighborhood, schools, and true market prices.</p>
          </Card>
          <Card>
            <h3>Lightning Responses</h3>
            <p>Average reply time under 5 minutes during business hours—much faster on chat.</p>
          </Card>
          <Card>
            <h3>Transparent Pricing</h3>
            <p>No hidden fees. We’ll walk you through taxes, utilities, and maintenance upfront.</p>
          </Card>
          <Card>
            <h3>End-to-End Help</h3>
            <p>From viewing to paperwork—we guide you through every step so nothing’s missed.</p>
          </Card>
        </Grid>

        <Stats>
          <Stat><strong>24/7</strong><span>Support</span></Stat>
          <Stat><strong>4.9/5</strong><span>Client rating</span></Stat>
          <Stat><strong>10k+</strong><span>Successful moves</span></Stat>
        </Stats>
      </FullBleed>
    </Section>
  );
};

export default HomeContactContainer;
