import React from "react";
import styled from "styled-components/macro";
import { Section } from "../components";

/* --- Full-bleed (kills side white space) --- */
const FullBleed = styled(Section.InnerContainer)`
  width: 100%;
  max-width: none;
  padding: clamp(48px, 8vw, 96px) clamp(16px, 3vw, 32px);
`;

/* --- Hero row --- */
const Hero = styled.div`
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  gap: clamp(24px, 4vw, 48px);
  align-items: center;

  @media (max-width: 980px) {
    grid-template-columns: 1fr;
  }
`;

const Copy = styled.div``;

const Eyebrow = styled.p`
  font-weight: 700;
  letter-spacing: .06em;
  text-transform: uppercase;
  color: var(--bs-primary, #2563eb);
  margin: 0 0 6px;
`;

const Title = styled.h2`
  margin: 0 0 10px;
  font-size: clamp(28px, 4vw, 42px);
  line-height: 1.15;
  color: var(--bs-gray-900, #111);
`;

const Lead = styled.p`
  margin: 10px 0 18px;
  max-width: 65ch;
  font-size: 1.1rem;
  line-height: 1.7;
  color: var(--bs-gray-700, #4b5563);
`;

/* --- Highlights (chips, info only) --- */
const Highlights = styled.ul`
  list-style: none;
  padding: 0;
  margin: 12px 0 0;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const Chip = styled.li`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 10px 12px;
  border-radius: 999px;
  background: #fff;
  border: 1px solid rgba(0,0,0,.08);
  color: var(--bs-gray-900, #111);
  font-weight: 600;
  font-size: .95rem;
`;

/* --- Right panel now contains real content --- */
const Panel = styled.div`
  position: relative;
  height: clamp(260px, 34vw, 420px);
  border-radius: 20px;
  background:
    radial-gradient(120% 120% at 10% 0%, rgba(37,99,235,.15), transparent 50%),
    radial-gradient(120% 120% at 90% 100%, rgba(16,185,129,.15), transparent 50%),
    linear-gradient(180deg, #ffffff, #f8fafc);
  box-shadow: 0 20px 40px rgba(0,0,0,.08);
  border: 1px solid rgba(0,0,0,.06);
  overflow: hidden;
  padding: 16px;
  display: grid;
  place-items: center;
`;

/* Inner layout: featured card + snapshot card */
const PanelGrid = styled.div`
  width: 100%;
  max-width: 560px;
  display: grid;
  grid-template-columns: 1.1fr .9fr;
  gap: 14px;

  @media (max-width: 520px) {
    grid-template-columns: 1fr;
  }
`;

/* --- Featured property preview --- */
const FeatureCard = styled.div`
  background: #fff;
  border-radius: 16px;
  border: 1px solid rgba(0,0,0,.06);
  box-shadow: 0 10px 20px rgba(0,0,0,.06);
  overflow: hidden;
`;

const Photo = styled.div`
  aspect-ratio: 4 / 3;
  background:
    linear-gradient(180deg, rgba(0,0,0,.08), transparent 40%),
    url("data:image/svg+xml;utf8,\
    <svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 800 600'>\
      <defs>\
        <linearGradient id='g' x1='0' y1='0' x2='1' y2='1'>\
          <stop offset='0%' stop-color='%23c7d2fe'/>\
          <stop offset='100%' stop-color='%23d1fae5'/>\
        </linearGradient>\
      </defs>\
      <rect width='800' height='600' fill='url(%23g)'/>\
      <g fill='%23ffffff' opacity='0.75'>\
        <rect x='90' y='120' width='260' height='180' rx='12'/>\
        <rect x='380' y='160' width='290' height='220' rx='12'/>\
        <rect x='140' y='340' width='500' height='110' rx='12'/>\
      </g>\
    </svg>") center/cover no-repeat;
`;

const PropMeta = styled.div`
  padding: 12px 14px 14px;

  h4 {
    margin: 0 0 4px;
    font-size: 1.05rem;
    color: var(--bs-gray-900, #111);
  }
  p {
    margin: 0;
    color: var(--bs-gray-700, #4b5563);
  }
`;

const MetaRow = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 8px;
  color: var(--bs-gray-700, #4b5563);
  font-size: .95rem;
`;

const Price = styled.div`
  margin-top: 10px;
  font-weight: 800;
  font-size: 1.1rem;
  color: var(--bs-gray-900, #111);
`;

/* --- Market snapshot --- */
const Snapshot = styled.div`
  background: #fff;
  border-radius: 16px;
  border: 1px solid rgba(0,0,0,.06);
  box-shadow: 0 10px 20px rgba(0,0,0,.06);
  padding: 14px;
  display: grid;
  gap: 10px;
`;

const SnapTitle = styled.div`
  font-weight: 700;
  color: var(--bs-gray-900, #111);
`;

const StatRow = styled.div`
  display: grid;
  grid-template-columns: 1fr auto;
  align-items: center;
  gap: 10px;
  font-size: .95rem;

  strong { color: var(--bs-gray-900, #111); }
  span { color: var(--bs-gray-600, #6b7280); }
`;

const Bar = styled.div`
  height: 8px;
  background: rgba(0,0,0,.06);
  border-radius: 6px;
  overflow: hidden;
  > i {
    display: block;
    height: 100%;
    width: ${p => p.$value || "0%"};
    background: linear-gradient(90deg, #93c5fd, #6ee7b7);
  }
`;

const Cities = styled.div`
  margin-top: 4px;
  display: grid;
  gap: 8px;
`;

const City = styled.div`
  display: grid;
  grid-template-columns: 1fr 60px;
  align-items: center;
  gap: 10px;

  small { color: var(--bs-gray-600, #6b7280); }
`;

const Grid = styled.div`
  margin-top: clamp(28px, 4vw, 44px);
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 18px;
`;

const Card = styled.div`
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 10px 24px rgba(0,0,0,.06);
  border: 1px solid rgba(0,0,0,.05);
  padding: 22px 22px 20px;
  transition: transform .15s ease, box-shadow .15s ease;
  &:hover { transform: translateY(-2px); box-shadow: 0 18px 36px rgba(0,0,0,.08); }

  h3 { margin: 0 0 8px; font-size: 1.15rem; font-weight: 700; color: var(--bs-gray-900, #111); }
  p  { margin: 0; color: var(--bs-gray-700, #4b5563); line-height: 1.65; }
`;

const Stats = styled.div`
  margin-top: 22px;
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 12px;
  @media (max-width: 640px) { grid-template-columns: 1fr 1fr; }
`;

const Stat = styled.div`
  background: #fff;
  border: 1px solid rgba(0,0,0,.06);
  border-radius: 14px;
  padding: 16px;
  text-align: center;
  strong { display:block; font-size: 1.35rem; }
  span { color: var(--bs-gray-600, #6b7280); }
`;

const HomeContactContainer = () => {
  return (
    <Section bgColor="--bs-fade-blue">
      <FullBleed>
        <Hero>
          <Copy>
            <Eyebrow>About the platform</Eyebrow>
            <Title>Everything you need to find your next home</Title>
            <Lead>
              Real Home simplifies buying, renting, and listing properties with verified
              data, rich neighborhood details, and a modern, map-first experience. Built for speed,
              clarity, and trust across the MENA region.
            </Lead>

            <Highlights>
              <Chip>‚úÖ Verified listings</Chip>
              <Chip>üîé Smart filters</Chip>
              <Chip>üó∫Ô∏è Real-time map view</Chip>
              <Chip>üè∑Ô∏è Transparent pricing & fees</Chip>
              <Chip>‚≠ê Save & compare</Chip>
              <Chip>üåô Light & dark themes</Chip>
            </Highlights>
          </Copy>

          {/* Right side now shows a featured property + snapshot (no buttons) */}
          <Panel>
            <PanelGrid>
              <FeatureCard>
                <Photo />
                <PropMeta>
                  <h4>3-Bedroom Apartment ‚Ä¢ New Listing</h4>
                  <p>üìç Amman ‚Ä¢ Abdoun ‚Ä¢ 125 m¬≤ ‚Ä¢ 2 Baths</p>
                  <MetaRow>
                    <span>üè† Balcony</span>
                    <span>üõó Elevator</span>
                    <span>üöó Parking</span>
                  </MetaRow>
                  <Price>JOD 120,000</Price>
                </PropMeta>
              </FeatureCard>

              <Snapshot>
                <SnapTitle>Market snapshot</SnapTitle>

                <StatRow>
                  <span>Active listings</span>
                  <strong>2,430</strong>
                </StatRow>
                <Bar $value="72%"><i /></Bar>

                <StatRow>
                  <span>Avg. rent (monthly)</span>
                  <strong>JOD 450</strong>
                </StatRow>
                <Bar $value="55%"><i /></Bar>

                <Cities>
                  <City>
                    <small>Amman</small>
                    <Bar $value="80%"><i /></Bar>
                  </City>
                  <City>
                    <small>Irbid</small>
                    <Bar $value="48%"><i /></Bar>
                  </City>
                  <City>
                    <small>Zarqa</small>
                    <Bar $value="36%"><i /></Bar>
                  </City>
                </Cities>
              </Snapshot>
            </PanelGrid>
          </Panel>
        </Hero>

        {/* Feature cards below (unchanged) */}
        <Grid>
          <Card>
            <h3>Accurate, Fresh Data</h3>
            <p>Listings are validated and refreshed frequently to keep prices, availability, and photos up to date.</p>
          </Card>
          <Card>
            <h3>Powerful Search</h3>
            <p>Filter by city, price, bedrooms, amenities, and more‚Äîthen explore results on an interactive map.</p>
          </Card>
          <Card>
            <h3>Neighborhood Insights</h3>
            <p>Understand commute times, nearby schools, services, and community vibes before you visit.</p>
          </Card>
          <Card>
            <h3>Saved Collections</h3>
            <p>Organize favorites into lists, add notes, and compare options side-by-side to decide confidently.</p>
          </Card>
          <Card>
            <h3>Clear Costs</h3>
            <p>Breakdowns for rent/sale price, deposits, utilities, and expected monthly totals‚Äîno surprises.</p>
          </Card>
          <Card>
            <h3>Privacy & Security</h3>
            <p>Modern authentication and secure data handling keep your account and personal info protected.</p>
          </Card>
        </Grid>

        <Stats>
          <Stat><strong>20+ cities</strong><span>Active coverage</span></Stat>
          <Stat><strong>4.9/5</strong><span>User satisfaction</span></Stat>
          <Stat><strong>10k+</strong><span>Successful moves</span></Stat>
        </Stats>
      </FullBleed>
    </Section>
  );
};

export default HomeContactContainer;
