import React, { useEffect, useState } from "react";
import styled from "styled-components/macro";
import { useDispatch, useSelector } from "react-redux";
import {
  HeaderContainer,
  ListingItemContainer,
  FooterContainer,
} from "../containers";
import { Section } from "../components";
import { getPropertyList } from "../redux/actions/propertiesAction";

const PageWrap = styled.div`
  width: min(1200px, 92%);
  margin: 0 auto;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
  gap: 24px;
  align-items: stretch; /* خلي الكروت بنفس الارتفاع */
`;

const SkeletonCard = styled.div`
  height: 320px;
  border-radius: 12px;
  background: linear-gradient(90deg, #f2f4f7 25%, #e9edf2 37%, #f2f4f7 63%);
  background-size: 400% 100%;
  animation: shimmer 1.2s infinite;
  box-shadow: var(--primary-box-shadow);
  @keyframes shimmer {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }
`;

const Listing = () => {
  const dispatch = useDispatch();
  const { loading, error, properties, page, pages, limit } =
    useSelector((s) => s.propertyList);

  const [firstLoad, setFirstLoad] = useState(true);

  useEffect(() => {
    dispatch(getPropertyList(1, 12));
  }, [dispatch]);

  useEffect(() => {
    if (!loading) setFirstLoad(false);
  }, [loading]);

  const loadMore = () => {
    if (page < pages) dispatch(getPropertyList(page + 1, limit));
  };

  return (
    <>
      <HeaderContainer bg="false" />

      <Section bgColor="--bs-fade-info" style={{ paddingBottom: 48 }}>
        <Section.InnerContainer>
          <PageWrap>
            <Section.Title style={{ textAlign: "center", marginBottom: 24 }}>
              Our Property List
            </Section.Title>

            <Grid>
              {/* Skeletons في أول تحميل */}
              {firstLoad && loading &&
                Array.from({ length: 12 }).map((_, i) => <SkeletonCard key={i} />)
              }

              {/* بيانات */}
              {!firstLoad &&
                properties.map((p) => <ListingItemContainer key={p.id} featured={p} />)
              }
            </Grid>

            {/* حالات خاصة */}
            {!loading && !error && properties.length === 0 && (
              <p style={{ textAlign: "center", marginTop: 24 }}>No properties found.</p>
            )}

            {error && (
              <p style={{ color: "crimson", textAlign: "center", marginTop: 24 }}>
                {error}
              </p>
            )}

            {/* زر تحميل المزيد */}
            {!loading && page < pages && (
              <Section.Footer style={{ display: "flex", justifyContent: "center", marginTop: 24 }}>
                <Section.Button onClick={loadMore}>More Listings</Section.Button>
              </Section.Footer>
            )}
          </PageWrap>
        </Section.InnerContainer>
      </Section>

      <FooterContainer />
    </>
  );
};

export default Listing;
