// containers/ListingItemContainer.jsx
import React from "react";
import { Listing } from "../components";

const ListingItemContainer = ({ featured, width }) => {
  const img = featured.imageUrl || "/placeholder.jpg";
  const loc = [featured.city, featured.state].filter(Boolean).join(", ");
  const desc = (featured.description || "").slice(0, 100);

  return (
    <Listing width={width}>
      <Listing.Top>
        <Listing.TopItem>
          {/* styled.img needs `src`, not `source` */}
          <Listing.Image src={img} alt={featured.title} />
          <Listing.TopItemContainer>
            <Listing.TopItemInfo>
              <Listing.Icon />
              <Listing.Text location>{loc}</Listing.Text>
            </Listing.TopItemInfo>
          </Listing.TopItemContainer>
        </Listing.TopItem>
      </Listing.Top>

      <Listing.Bottom>
        <Listing.BottomItem>
          <Listing.Title>
            <Listing.Anchor to={`/property/${featured.id}`}>
              {featured.title}
            </Listing.Anchor>
          </Listing.Title>

          <Listing.Price>JOD {Number(featured.price || 0).toLocaleString()}</Listing.Price>
          <Listing.Text description>{desc}</Listing.Text>

          <Listing.Button>
            <Listing.Anchor to={`/property/${featured.id}`}>Details</Listing.Anchor>
          </Listing.Button>

          <Listing.AgentContainer />
        </Listing.BottomItem>
      </Listing.Bottom>
    </Listing>
  );
};

export default ListingItemContainer;
