import React, { Fragment, useState } from "react";
import { useDispatch } from "react-redux";
import { HeaderContainer, FooterContainer } from "../containers";
import { Login as LoginBlock, Form } from "../components";
import { login } from "../redux/actions/authActions";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState(null);

  const onChange = (e) => setForm((f) => ({ ...f, [e.target.name]: e.target.value }));


const onSubmit = async (e) => {


  console.log("Submitting login:", { email: form.email, password: form.password });

  e.preventDefault();
  setError(null);

  try {
    const res = await fetch("http://localhost:7542/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email: form.email, password: form.password }),
      credentials: "include", // ✅ allow cookies (JWT token)
    });

    const data = await res.json();
    if (!res.ok) throw new Error(data.message || "Login failed");

    navigate("/");       // ✅ redirect to home page
    alert(data.message); // "✅ Login successful"
    // TODO: redirect to dashboard
    // history.push("/dashboard");

  } catch (err) {
    setError(err.message);
  }
};

  return (
    <Fragment>
      <HeaderContainer bg="false" />
      <LoginBlock>
        <LoginBlock.Container>
          <LoginBlock.Content>
            <LoginBlock.Header>
              <LoginBlock.Title>Login</LoginBlock.Title>
            </LoginBlock.Header>

            <LoginBlock.InnerContent>
              <Form onSubmit={onSubmit}>
                <Form.FormGroup>
                  <Form.Label>Email</Form.Label>
                  <Form.Input name="email" value={form.email} onChange={onChange} placeholder="you@example.com" />
                </Form.FormGroup>

                <Form.FormGroup>
                  <Form.Label>Password</Form.Label>
                  <Form.Input name="password" type="password" value={form.password} onChange={onChange} />
                </Form.FormGroup>

                {error && <p style={{ color: "crimson" }}>{error}</p>}

                <Form.FormGroup>
                  <Form.SubmitInput type="submit" value="Login" />
                </Form.FormGroup>
              </Form>
            </LoginBlock.InnerContent>

            <LoginBlock.Footer>
              <LoginBlock.Text>
                <LoginBlock.Anchor to="/forgot-password">Forgot Password?</LoginBlock.Anchor>
              </LoginBlock.Text>
              <LoginBlock.Text>
                Don’t have an account? <LoginBlock.Anchor to="/signup">Sign Up</LoginBlock.Anchor>
              </LoginBlock.Text>
            </LoginBlock.Footer>
          </LoginBlock.Content>
        </LoginBlock.Container>
      </LoginBlock>
      <FooterContainer />
    </Fragment>
  );
};

export default LoginPage;
